	)) {
		echo $error_message;
		echo '<a href="authorizations_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('authorizations', $data['selectedID'], calculated_fields()['authorizations']);

	// hook: authorizations_after_update
	if(function_exists('authorizations_after_update')) {
		$res = sql("SELECT * FROM `authorizations` WHERE `authorize_id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

