function billing_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('billing');
	if(!$arrPerm['insert']) return false;

	$data = [
		'district' => Request::lookup('district', ''),
		'location' => Request::lookup('district'),
		'upload_invoice' => Request::fileUpload('upload_invoice', [
			'maxSize' => 5365760,
			'types' => 'txt|doc|docx|docm|odt|pdf|rtf',
			'noRename' => true,
			'dir' => '',
			'success' => function($name, $selected_id) {
			},
			'failure' => function($selected_id, $fileRemoved) {
				if(!strlen(Request::val('SelectedID'))) return '';

				/* for empty upload fields, when saving a copy of an existing record, copy the original upload field */
				return existing_value('billing', 'upload_invoice', Request::val('SelectedID'));
			},
		]),
		'job_card_number' => Request::lookup('job_card_number', ''),
		'invoice_date' => Request::dateComponents('invoice_date', '0000-00-00'),
		'maintenance_file' => Request::val('maintenance_file', ''),
		'vehicle_registration_number' => Request::lookup('vehicle_registration_number', ''),
		'engine_number' => Request::lookup('vehicle_registration_number'),
		'make_of_vehicle' => Request::lookup('vehicle_registration_number'),
	];


	// hook: billing_before_insert
	if(function_exists('billing_before_insert')) {
		$args = [];
		if(!billing_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('billing', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('billing', $recID, calculated_fields()['billing']);

	// hook: billing_after_insert
	if(function_exists('billing_after_insert')) {
		$res = sql("SELECT * FROM `billing` WHERE `billing_id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!billing_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('billing', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) billing_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

