}

function billing_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('billing', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: billing_before_delete
	if(function_exists('billing_before_delete')) {
		$args = [];
		if(!billing_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: '' 
			);
	}

	sql("DELETE FROM `billing` WHERE `billing_id`='{$selected_id}'", $eo);

	// hook: billing_after_delete
	if(function_exists('billing_after_delete')) {
		$args = [];
		billing_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='billing' AND `pkValue`='{$selected_id}'", $eo);
}

function billing_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('billing', $selected_id, 'edit')) return false;

	$data = [
		'district' => Request::lookup('district', ''),
		'location' => Request::lookup('district'),
		'upload_invoice' => Request::fileUpload('upload_invoice', [
			'maxSize' => 5365760,
			'types' => 'txt|doc|docx|docm|odt|pdf|rtf',
			'noRename' => true,
			'dir' => '',
			'id' => $selected_id,
			'success' => function($name, $selected_id) {
			},
			'removeOnRequest' => true,
			'remove' => function($selected_id) {
				// do nothing: preserve removed files on server.
			},
			'failure' => function($selected_id, $fileRemoved) {
				if($fileRemoved) return '';
				return existing_value('billing', 'upload_invoice', $selected_id);
			},
		]),
		'job_card_number' => Request::lookup('job_card_number', ''),
		'invoice_date' => Request::dateComponents('invoice_date', ''),
		'maintenance_file' => Request::val('maintenance_file', ''),
		'vehicle_registration_number' => Request::lookup('vehicle_registration_number', ''),
		'engine_number' => Request::lookup('vehicle_registration_number'),
		'make_of_vehicle' => Request::lookup('vehicle_registration_number'),
	];

	// get existing values
	$old_data = getRecord('billing', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: billing_before_update
	if(function_exists('billing_before_update')) {
		$args = ['old_data' => $old_data];
		if(!billing_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'billing', 
		backtick_keys_once($set), 
		['`billing_id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="billing_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('billing', $data['selectedID'], calculated_fields()['billing']);

	// hook: billing_after_update
	if(function_exists('billing_after_update')) {
		$res = sql("SELECT * FROM `billing` WHERE `billing_id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['billing_id'];
		$args = ['old_data' => $old_data];
		if(!billing_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='billing' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

