		$jdata = get_joined_record('external_repairs_mechanical', $selected_id);
		if($jdata === false) $jdata = get_defaults('external_repairs_mechanical');
		$rdata = $row;
	}
	$templateCode .= loadView('external_repairs_mechanical-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: external_repairs_mechanical_dv
	if(function_exists('external_repairs_mechanical_dv')) {
		$args = [];
		external_repairs_mechanical_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}