	}
	$templateCode .= loadView('gate_security-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: gate_security_dv
	if(function_exists('gate_security_dv')) {
		$args = [];
		gate_security_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
