function gmt_fleet_register_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('gmt_fleet_register', $selected_id, 'edit')) return false;

	$data = [
		'vehicle_registration_number' => Request::val('vehicle_registration_number', ''),
		'register_number' => Request::val('register_number', ''),
		'engine_number' => Request::val('engine_number', ''),
		'chassis_number' => Request::val('chassis_number', ''),
		'dealer_name' => Request::lookup('dealer_name', ''),
		'make_of_vehicle' => Request::lookup('make_of_vehicle', ''),
		'model_of_vehicle' => Request::val('model_of_vehicle', ''),
		'year_model_specification' => Request::lookup('year_model_specification', ''),
		'engine_capacity' => Request::val('engine_capacity', ''),
		'tyre_size' => Request::val('tyre_size', ''),
		'transmission' => Request::lookup('transmission', ''),
		'fuel_type' => Request::lookup('fuel_type', ''),
		'type_of_vehicle' => Request::lookup('type_of_vehicle', ''),
		'colour_of_vehicle' => Request::lookup('colour_of_vehicle', ''),
		'application_status' => Request::lookup('application_status', ''),
		'case_number' => Request::val('case_number', ''),
		'barcode_number' => Request::val('barcode_number', ''),
		'purchase_price' => Request::val('purchase_price', ''),
		'depreciation_value' => Request::val('depreciation_value', ''),
		'photo_of_vehicle' => Request::fileUpload('photo_of_vehicle', [
			'maxSize' => 1048576,
			'types' => 'jpg|jpeg|gif|png|webp',
			'noRename' => true,
			'dir' => '',
			'id' => $selected_id,
			'success' => function($name, $selected_id) {
				createThumbnail($name, getThumbnailSpecs('gmt_fleet_register', 'photo_of_vehicle', 'tv'));
				createThumbnail($name, getThumbnailSpecs('gmt_fleet_register', 'photo_of_vehicle', 'dv'));
			},
			'removeOnRequest' => true,
			'remove' => function($selected_id) {
				// do nothing: preserve removed files on server.
			},
			'failure' => function($selected_id, $fileRemoved) {
				if($fileRemoved) return '';
				return existing_value('gmt_fleet_register', 'photo_of_vehicle', $selected_id);
			},
		]),
		'user_name_and_surname' => Request::val('user_name_and_surname', ''),
		'user_contact_email' => Request::val('user_contact_email', ''),
		'contact_number' => Request::val('contact_number', ''),
