		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: internal_repairs_body_before_update
	if(function_exists('internal_repairs_body_before_update')) {
		$args = ['old_data' => $old_data];
		if(!internal_repairs_body_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'internal_repairs_body', 
		backtick_keys_once($set), 
		['`internal_repairs_body_id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="internal_repairs_body_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('internal_repairs_body', $data['selectedID'], calculated_fields()['internal_repairs_body']);

	// hook: internal_repairs_body_after_update
	if(function_exists('internal_repairs_body_after_update')) {
		$res = sql("SELECT * FROM `internal_repairs_body` WHERE `internal_repairs_body_id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['internal_repairs_body_id'];
		$args = ['old_data' => $old_data];
		if(!internal_repairs_body_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='internal_repairs_body' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

