function manufacturer_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('manufacturer');
	if(!$arrPerm['insert']) return false;

	$data = [
		'manufacturer_type' => Request::lookup('manufacturer_type', ''),
		'manufacturer_name' => Request::val('manufacturer_name', ''),
		'contact_person' => Request::val('contact_person', ''),
		'contact_details' => Request::val('contact_details', ''),
		'contact_email' => Request::val('contact_email', ''),
	];


	// hook: manufacturer_before_insert
	if(function_exists('manufacturer_before_insert')) {
		$args = [];
		if(!manufacturer_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('manufacturer', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('manufacturer', $recID, calculated_fields()['manufacturer']);

	// hook: manufacturer_after_insert
	if(function_exists('manufacturer_after_insert')) {
		$res = sql("SELECT * FROM `manufacturer` WHERE `manufacturer_id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!manufacturer_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('manufacturer', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) manufacturer_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

function manufacturer_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);

	// launch requests, asynchronously
	curl_batch($requests);
}

