function manufacturer_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('manufacturer', $selected_id, 'edit')) return false;

	$data = [
		'manufacturer_type' => Request::lookup('manufacturer_type', ''),
		'manufacturer_name' => Request::val('manufacturer_name', ''),
		'contact_person' => Request::val('contact_person', ''),
		'contact_details' => Request::val('contact_details', ''),
		'contact_email' => Request::val('contact_email', ''),
	];

	// get existing values
	$old_data = getRecord('manufacturer', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: manufacturer_before_update
	if(function_exists('manufacturer_before_update')) {
		$args = ['old_data' => $old_data];
		if(!manufacturer_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'manufacturer', 
		backtick_keys_once($set), 
		['`manufacturer_id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="manufacturer_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('manufacturer', $data['selectedID'], calculated_fields()['manufacturer']);

	// hook: manufacturer_after_update
	if(function_exists('manufacturer_after_update')) {
		$res = sql("SELECT * FROM `manufacturer` WHERE `manufacturer_id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['manufacturer_id'];
		$args = ['old_data' => $old_data];
		if(!manufacturer_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='manufacturer' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

function manufacturer_form($selected_id = '', $AllowUpdate = 1, $AllowInsert = 1, $AllowDelete = 1, $separateDV = 0, $TemplateDV = '', $TemplateDVP = '') {
