function merchant_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('merchant');
	if(!$arrPerm['insert']) return false;

	$data = [
		'merchant_type' => Request::lookup('merchant_type', ''),
		'merchant_code' => Request::val('merchant_code', ''),
		'merchant_name' => Request::val('merchant_name', ''),
		'merchant_contact_email' => Request::val('merchant_contact_email', ''),
		'merchant_street_address' => Request::val('merchant_street_address', ''),
		'merchant_suburb' => Request::val('merchant_suburb', ''),
		'merchant_city' => Request::val('merchant_city', ''),
		'merchant_address_code' => Request::val('merchant_address_code', ''),
		'merchant_contact_details' => Request::val('merchant_contact_details', ''),
	];


	// hook: merchant_before_insert
	if(function_exists('merchant_before_insert')) {
		$args = [];
		if(!merchant_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('merchant', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('merchant', $recID, calculated_fields()['merchant']);

	// hook: merchant_after_insert
	if(function_exists('merchant_after_insert')) {
		$res = sql("SELECT * FROM `merchant` WHERE `merchant_id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!merchant_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('merchant', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) merchant_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

function merchant_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);

	// launch requests, asynchronously
	curl_batch($requests);
}

