function merchant_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('merchant', $selected_id, 'edit')) return false;

	$data = [
		'merchant_type' => Request::lookup('merchant_type', ''),
		'merchant_code' => Request::val('merchant_code', ''),
		'merchant_name' => Request::val('merchant_name', ''),
		'merchant_contact_email' => Request::val('merchant_contact_email', ''),
		'merchant_street_address' => Request::val('merchant_street_address', ''),
		'merchant_suburb' => Request::val('merchant_suburb', ''),
		'merchant_city' => Request::val('merchant_city', ''),
		'merchant_address_code' => Request::val('merchant_address_code', ''),
		'merchant_contact_details' => Request::val('merchant_contact_details', ''),
	];

	// get existing values
	$old_data = getRecord('merchant', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: merchant_before_update
	if(function_exists('merchant_before_update')) {
		$args = ['old_data' => $old_data];
		if(!merchant_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'merchant', 
		backtick_keys_once($set), 
		['`merchant_id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="merchant_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('merchant', $data['selectedID'], calculated_fields()['merchant']);

	// hook: merchant_after_update
	if(function_exists('merchant_after_update')) {
		$res = sql("SELECT * FROM `merchant` WHERE `merchant_id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['merchant_id'];
		$args = ['old_data' => $old_data];
		if(!merchant_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='merchant' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

function merchant_form($selected_id = '', $AllowUpdate = 1, $AllowInsert = 1, $AllowDelete = 1, $separateDV = 0, $TemplateDV = '', $TemplateDVP = '') {
