	}

	if($selected_id) {
		if(!Request::val('Embedded')) $templateCode = str_replace('<%%DVPRINT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="dvprint" name="dvprint_x" value="1" onclick="$j(\'form\').eq(0).prop(\'novalidate\', true); document.myform.reset(); return true;" title="' . html_attr($Translation['Print Preview']) . '"><i class="glyphicon glyphicon-print"></i> ' . $Translation['Print Preview'] . '</button>', $templateCode);
		if($AllowUpdate) {
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '<button type="submit" class="btn btn-success btn-lg" id="update" name="update_x" value="1" onclick="return merchant_validateData();" title="' . html_attr($Translation['Save Changes']) . '"><i class="glyphicon glyphicon-ok"></i> ' . $Translation['Save Changes'] . '</button>', $templateCode);
		} else {
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		}
		if(($arrPerm['delete'] == 1 && $ownerMemberID == getLoggedMemberID()) || ($arrPerm['delete'] == 2 && $ownerGroupID == getLoggedGroupID()) || $arrPerm['delete'] == 3) { // allow delete?
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '<button type="submit" class="btn btn-danger" id="delete" name="delete_x" value="1" onclick="return confirm(\'' . $Translation['are you sure?'] . '\');" title="' . html_attr($Translation['Delete']) . '"><i class="glyphicon glyphicon-trash"></i> ' . $Translation['Delete'] . '</button>', $templateCode);
		} else {
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		}
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>', $templateCode);
	} else {
		$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);

		// if not in embedded mode and user has insert only but no view/update/delete,
		// remove 'back' button
		if(
			$arrPerm['insert']
			&& !$arrPerm['update'] && !$arrPerm['delete'] && !$arrPerm['view']
			&& !Request::val('Embedded')
		)
			$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '', $templateCode);
		elseif($separateDV)
			$templateCode = str_replace(
				'<%%DESELECT_BUTTON%%>', 
				'<button
					type="submit" 
					class="btn btn-default" 
					id="deselect" 
					name="deselect_x" 
					value="1" 
					onclick="' . $backAction . '" 
					title="' . html_attr($Translation['Back']) . '">
						<i class="glyphicon glyphicon-chevron-left"></i> ' .
						$Translation['Back'] .
				'</button>',
				$templateCode
			);
		else
			$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '', $templateCode);
	}

	// set records to read only if user can't insert new records and can't edit current record
	if(($selected_id && !$AllowUpdate) || (!$selected_id && !$AllowInsert)) {
		$jsReadOnly = '';
		$jsReadOnly .= "\tjQuery('#merchant_type').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#merchant_type_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#merchant_code').replaceWith('<div class=\"form-control-static\" id=\"merchant_code\">' + (jQuery('#merchant_code').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#merchant_name').replaceWith('<div class=\"form-control-static\" id=\"merchant_name\">' + (jQuery('#merchant_name').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#merchant_contact_email').replaceWith('<div class=\"form-control-static\" id=\"merchant_contact_email\">' + (jQuery('#merchant_contact_email').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#merchant_contact_email, #merchant_contact_email-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('#merchant_suburb').replaceWith('<div class=\"form-control-static\" id=\"merchant_suburb\">' + (jQuery('#merchant_suburb').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#merchant_city').replaceWith('<div class=\"form-control-static\" id=\"merchant_city\">' + (jQuery('#merchant_city').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#merchant_address_code').replaceWith('<div class=\"form-control-static\" id=\"merchant_address_code\">' + (jQuery('#merchant_address_code').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#merchant_contact_details').replaceWith('<div class=\"form-control-static\" id=\"merchant_contact_details\">' + (jQuery('#merchant_contact_details').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	} elseif(($AllowInsert && !$selected_id) || ($AllowUpdate && $selected_id)) {
		$jsEditable = "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(merchant_type)%%>', $combo_merchant_type->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(merchant_type)%%>', $combo_merchant_type->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(merchant_type)%%>', urlencode($combo_merchant_type->MatchText), $templateCode);

	/* lookup fields array: 'lookup field name' => ['parent table name', 'lookup field caption'] */
	$lookup_fields = ['merchant_type' => ['merchant_type', 'Merchant Type:'], ];
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] /* && !Request::val('Embedded')*/) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-default add_new_parent" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus text-success"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(merchant_id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(merchant_type)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(merchant_code)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(merchant_name)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(merchant_contact_email)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(merchant_street_address)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(merchant_suburb)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(merchant_city)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(merchant_address_code)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(merchant_contact_details)%%>', '', $templateCode);

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(merchant_id)%%>', safe_html($urow['merchant_id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(merchant_id)%%>', html_attr($row['merchant_id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(merchant_id)%%>', urlencode($urow['merchant_id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(merchant_type)%%>', safe_html($urow['merchant_type']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(merchant_type)%%>', html_attr($row['merchant_type']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(merchant_type)%%>', urlencode($urow['merchant_type']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(merchant_code)%%>', safe_html($urow['merchant_code']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(merchant_code)%%>', html_attr($row['merchant_code']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(merchant_code)%%>', urlencode($urow['merchant_code']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(merchant_name)%%>', safe_html($urow['merchant_name']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(merchant_name)%%>', html_attr($row['merchant_name']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(merchant_name)%%>', urlencode($urow['merchant_name']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(merchant_contact_email)%%>', safe_html($urow['merchant_contact_email']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(merchant_contact_email)%%>', html_attr($row['merchant_contact_email']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(merchant_contact_email)%%>', urlencode($urow['merchant_contact_email']), $templateCode);
		if($AllowUpdate || $AllowInsert) {
			$templateCode = str_replace('<%%HTMLAREA(merchant_street_address)%%>', '<textarea name="merchant_street_address" id="merchant_street_address" rows="5">' . safe_html(htmlspecialchars_decode($row['merchant_street_address'])) . '</textarea>', $templateCode);
		} else {
			$templateCode = str_replace('<%%HTMLAREA(merchant_street_address)%%>', '<div id="merchant_street_address" class="form-control-static">' . $row['merchant_street_address'] . '</div>', $templateCode);
		}
		$templateCode = str_replace('<%%VALUE(merchant_street_address)%%>', nl2br($row['merchant_street_address']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(merchant_street_address)%%>', urlencode($urow['merchant_street_address']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(merchant_suburb)%%>', safe_html($urow['merchant_suburb']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(merchant_suburb)%%>', html_attr($row['merchant_suburb']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(merchant_suburb)%%>', urlencode($urow['merchant_suburb']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(merchant_city)%%>', safe_html($urow['merchant_city']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(merchant_city)%%>', html_attr($row['merchant_city']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(merchant_city)%%>', urlencode($urow['merchant_city']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(merchant_address_code)%%>', safe_html($urow['merchant_address_code']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(merchant_address_code)%%>', html_attr($row['merchant_address_code']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(merchant_address_code)%%>', urlencode($urow['merchant_address_code']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(merchant_contact_details)%%>', safe_html($urow['merchant_contact_details']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(merchant_contact_details)%%>', html_attr($row['merchant_contact_details']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(merchant_contact_details)%%>', urlencode($urow['merchant_contact_details']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(merchant_id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(merchant_id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(merchant_type)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(merchant_type)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(merchant_code)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(merchant_code)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(merchant_name)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(merchant_name)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(merchant_contact_email)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(merchant_contact_email)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%HTMLAREA(merchant_street_address)%%>', '<textarea name="merchant_street_address" id="merchant_street_address" rows="5"></textarea>', $templateCode);
		$templateCode = str_replace('<%%VALUE(merchant_suburb)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(merchant_suburb)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(merchant_city)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(merchant_city)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(merchant_address_code)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(merchant_address_code)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(merchant_contact_details)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(merchant_contact_details)%%>', urlencode(''), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if(Request::val('dvprint_x') == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
			$templateCode.="\n\tif(document.getElementById('merchant_contact_emailEdit')) { document.getElementById('merchant_contact_emailEdit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('merchant_contact_emailEditLink')) { document.getElementById('merchant_contact_emailEditLink').style.display='none'; }";
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields
	$filterField = Request::val('FilterField');
	$filterOperator = Request::val('FilterOperator');
	$filterValue = Request::val('FilterValue');

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('merchant');
	if($selected_id) {
		$jdata = get_joined_record('merchant', $selected_id);
		if($jdata === false) $jdata = get_defaults('merchant');
		$rdata = $row;
	}
	$templateCode .= loadView('merchant-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: merchant_dv
	if(function_exists('merchant_dv')) {
		$args = [];
		merchant_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}