function parts_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('parts');
	if(!$arrPerm['insert']) return false;

	$data = [
		'part_type' => Request::lookup('part_type', ''),
		'part_number' => Request::val('part_number', ''),
		'part_name' => Request::val('part_name', ''),
		'description' => Request::val('description', ''),
		'manufacturer' => Request::lookup('manufacturer', ''),
		'dealer' => Request::lookup('dealer', ''),
		'measure' => Request::val('measure', ''),
		'unit_price' => Request::val('unit_price', '0.00'),
		'quantity' => Request::val('quantity', ''),
		'freight' => Request::val('freight', ''),
		'amount' => Request::val('amount', '0.00'),
		'tax' => Request::val('tax', ''),
		'total_amount' => Request::val('total_amount', '0.00'),
		'discount_price' => Request::val('discount_price', '0.00'),
		'net_part_price' => Request::val('net_part_price', '0.00'),
	];


	// hook: parts_before_insert
	if(function_exists('parts_before_insert')) {
		$args = [];
		if(!parts_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('parts', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('parts', $recID, calculated_fields()['parts']);

	// hook: parts_after_insert
	if(function_exists('parts_after_insert')) {
		$res = sql("SELECT * FROM `parts` WHERE `parts_id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!parts_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('parts', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) parts_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

