function parts_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('parts', $selected_id, 'edit')) return false;

	$data = [
		'part_type' => Request::lookup('part_type', ''),
		'part_number' => Request::val('part_number', ''),
		'part_name' => Request::val('part_name', ''),
		'description' => Request::val('description', ''),
		'manufacturer' => Request::lookup('manufacturer', ''),
		'dealer' => Request::lookup('dealer', ''),
		'measure' => Request::val('measure', ''),
		'unit_price' => Request::val('unit_price', ''),
		'quantity' => Request::val('quantity', ''),
		'freight' => Request::val('freight', ''),
		'amount' => Request::val('amount', ''),
		'tax' => Request::val('tax', ''),
		'total_amount' => Request::val('total_amount', ''),
		'discount_price' => Request::val('discount_price', ''),
		'net_part_price' => Request::val('net_part_price', ''),
	];

	// get existing values
	$old_data = getRecord('parts', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: parts_before_update
	if(function_exists('parts_before_update')) {
		$args = ['old_data' => $old_data];
		if(!parts_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'parts', 
		backtick_keys_once($set), 
		['`parts_id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="parts_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('parts', $data['selectedID'], calculated_fields()['parts']);

	// hook: parts_after_update
	if(function_exists('parts_after_update')) {
		$res = sql("SELECT * FROM `parts` WHERE `parts_id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['parts_id'];
		$args = ['old_data' => $old_data];
		if(!parts_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='parts' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

