function service_records_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('service_records', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: service_records_before_delete
	if(function_exists('service_records_before_delete')) {
		$args = [];
		if(!service_records_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: '' 
			);
	}

	sql("DELETE FROM `service_records` WHERE `records_id`='{$selected_id}'", $eo);

	// hook: service_records_after_delete
	if(function_exists('service_records_after_delete')) {
		$args = [];
		service_records_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='service_records' AND `pkValue`='{$selected_id}'", $eo);
}

