		if( $dvprint) $templateCode = str_replace('<%%VALUE(document_3)%%>', safe_html($urow['document_3']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(document_3)%%>', html_attr($row['document_3']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(document_3)%%>', urlencode($urow['document_3']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(document_4)%%>', safe_html($urow['document_4']), $templateCode);
