function service_type_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('service_type');
	if(!$arrPerm['insert']) return false;

	$data = [
		'service' => Request::val('service', ''),
		'type_of_service' => br2nl(Request::val('type_of_service', '')),
		'reference' => Request::val('reference', ''),
		'service_item_type' => Request::lookup('service_item_type', ''),
		'service_category' => Request::lookup('service_category', ''),
		'service_item' => Request::val('service_item', ''),
		'frequency_time_number' => Request::val('frequency_time_number', ''),
		'frequency_time' => Request::val('frequency_time', ''),
		'frequency_odometer' => Request::val('frequency_odometer', ''),
	];


	// hook: service_type_before_insert
	if(function_exists('service_type_before_insert')) {
		$args = [];
		if(!service_type_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('service_type', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('service_type', $recID, calculated_fields()['service_type']);

	// hook: service_type_after_insert
	if(function_exists('service_type_after_insert')) {
		$res = sql("SELECT * FROM `service_type` WHERE `service_type_id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!service_type_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('service_type', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) service_type_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

function service_type_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);

	// launch requests, asynchronously
	curl_batch($requests);
}

function service_type_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('service_type', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: service_type_before_delete
	if(function_exists('service_type_before_delete')) {
		$args = [];
		if(!service_type_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: '' 
			);
	}

	sql("DELETE FROM `service_type` WHERE `service_type_id`='{$selected_id}'", $eo);

	// hook: service_type_after_delete
	if(function_exists('service_type_after_delete')) {
		$args = [];
		service_type_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='service_type' AND `pkValue`='{$selected_id}'", $eo);
}

function service_type_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('service_type', $selected_id, 'edit')) return false;

	$data = [
		'service' => Request::val('service', ''),
		'type_of_service' => br2nl(Request::val('type_of_service', '')),
		'reference' => Request::val('reference', ''),
		'service_item_type' => Request::lookup('service_item_type', ''),
		'service_category' => Request::lookup('service_category', ''),
		'service_item' => Request::val('service_item', ''),
		'frequency_time_number' => Request::val('frequency_time_number', ''),
		'frequency_time' => Request::val('frequency_time', ''),
		'frequency_odometer' => Request::val('frequency_odometer', ''),
	];

	// get existing values
	$old_data = getRecord('service_type', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: service_type_before_update
	if(function_exists('service_type_before_update')) {
		$args = ['old_data' => $old_data];
		if(!service_type_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'service_type', 
		backtick_keys_once($set), 
		['`service_type_id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="service_type_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('service_type', $data['selectedID'], calculated_fields()['service_type']);

	// hook: service_type_after_update
	if(function_exists('service_type_after_update')) {
		$res = sql("SELECT * FROM `service_type` WHERE `service_type_id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['service_type_id'];
		$args = ['old_data' => $old_data];
		if(!service_type_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='service_type' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

