	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('vehicle_return_check_list');
	if($selected_id) {
		$jdata = get_joined_record('vehicle_return_check_list', $selected_id);
		if($jdata === false) $jdata = get_defaults('vehicle_return_check_list');
		$rdata = $row;
	}
	$templateCode .= loadView('vehicle_return_check_list-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: vehicle_return_check_list_dv
	if(function_exists('vehicle_return_check_list_dv')) {
		$args = [];
		vehicle_return_check_list_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}