function work_allocation_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('work_allocation', $selected_id, 'edit')) return false;

	$data = [
		'district' => Request::lookup('district', ''),
		'location' => Request::lookup('district'),
		'cost_centre' => Request::lookup('cost_centre', ''),
		'supervisor_name_and_surname' => Request::val('supervisor_name_and_surname', ''),
		'supervisor_contact_details' => Request::val('supervisor_contact_details', ''),
		'supervisor_email_address' => Request::val('supervisor_email_address', ''),
		'supervisor_signature' => Request::fileUpload('supervisor_signature', [
			'maxSize' => 512000,
			'types' => 'jpg|jpeg|gif|png|webp',
			'noRename' => false,
			'dir' => '',
			'id' => $selected_id,
			'success' => function($name, $selected_id) {
				createThumbnail($name, getThumbnailSpecs('work_allocation', 'supervisor_signature', 'tv'));
				createThumbnail($name, getThumbnailSpecs('work_allocation', 'supervisor_signature', 'dv'));
			},
			'removeOnRequest' => true,
			'remove' => function($selected_id) {
				// do nothing: preserve removed files on server.
			},
			'failure' => function($selected_id, $fileRemoved) {
				if($fileRemoved) return '';
				return existing_value('work_allocation', 'supervisor_signature', $selected_id);
			},
		]),
		'economical_repair' => Request::val('economical_repair', ''),
		'uneconomical_repair' => Request::val('uneconomical_repair', ''),
		'work_allocation_reference_number' => Request::val('work_allocation_reference_number', ''),
		'vehicle_registration_number' => Request::lookup('vehicle_registration_number', ''),
		'engine_number' => Request::lookup('vehicle_registration_number'),
		'make_of_vehicle' => Request::lookup('vehicle_registration_number'),
		'date_captured' => mysql_datetime(Request::val('date_captured', '')),
	];

	// get existing values
	$old_data = getRecord('work_allocation', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: work_allocation_before_update
	if(function_exists('work_allocation_before_update')) {
		$args = ['old_data' => $old_data];
		if(!work_allocation_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'work_allocation', 
		backtick_keys_once($set), 
		['`work_allocation_id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="work_allocation_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('work_allocation', $data['selectedID'], calculated_fields()['work_allocation']);

	// hook: work_allocation_after_update
	if(function_exists('work_allocation_after_update')) {
		$res = sql("SELECT * FROM `work_allocation` WHERE `work_allocation_id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['work_allocation_id'];
		$args = ['old_data' => $old_data];
		if(!work_allocation_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='work_allocation' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

